import {Col, Container, Row} from "reactstrap";
import image16 from "../../ICON/SamLam.png";
import image20 from "../../ICON/AlexCheng.png";
import image2 from "../../ICON/AlexLuk.png";
import image3 from "../../ICON/AnsonLam.png";
import image4 from "../../ICON/Chan man Fung.png";
import image5 from "../../ICON/CHAN Wai Lam.png";
import image6 from "../../ICON/ChoiCheukLam.png";
import image7 from "../../ICON/FokWaiYin.png";
import image8 from "../../ICON/HugoCheng.png";
import image9 from "../../ICON/HuiKaiHin.png";
import image10 from "../../ICON/JackChan.png";
import image11 from "../../ICON/JackyHo.png";
import image12 from "../../ICON/LawMeiChing.png";
import image14 from "../../ICON/PunChoYan.png";
import image15 from "../../ICON/RonaldLeung.png";
import image17 from "../../ICON/TIAN Li.png";
import image18 from "../../ICON/TsuiKaYan.png";
import image19 from "../../ICON/WONG Chiu Ching.png";
import image13 from "../../ICON/MakYeungFung.png";
import React from "react";
import "./MainContent.css"
import styled from "styled-components";

function MainContent (image, avatar, name, price, title, ctaButton) {
    return(
        <div className="g-card">
            <div className="inner-content">
                <div className="image">
                    <img src={image} alt="" />
                    <div className="name">
                        <img src={avatar} alt="" />
                        <p>{name}</p>
                    </div>
                </div>
                <div className="card-content">
                    <h6 className="card-title">{title}</h6>
                    <div className="duration">

                    </div>
                    <div className="cta-btn">
                        {ctaButton}
                    </div>
                </div>
            </div>
        </div>
    );
}

const GradientCardStyled = styled.div`
    border-radius: 20px;
    font-size: 1rem;
    transition: all 1s ease-in-out;
    background: linear-gradient(130deg,#eb3fa9,#395ff6 50%,#eb3fa9);
    animation: colors 3s infinite ease-in-out;
    @keyframes colors {
        0%{
            background: linear-gradient(130deg,#eb3fa9,#395ff6 50%,#eb3fa9);
        }
        50%{
            background: linear-gradient(90deg, #7F41DB 0%, #022894 100%);
        }
        70%{
            background: linear-gradient(130deg,#022894  0%, #7F41DB 100%);
        }
        100%{
            background: linear-gradient(130deg,#eb3fa9,#395ff6 50%,#eb3fa9);
        }
    }
    .g-card{
        margin: .2rem;
        .inner-content{
            background-color: #091026;
            padding: .8rem;
            border-radius: 20px;
        }
        .image{
            width: 100%;
            position: relative;
            img:first-child{
                width: 100%;
                object-fit: cover;
                height: 300px;
                border-radius: 10px;
            }
            .name{
                position: absolute;
                left: 50%;
                bottom: -26px;
                background-color: #03091f;
                display: flex;
                align-items: center;
                transform: translateX(-50%);
                width: 75%;
                padding: .5rem .5rem;
                border-radius: 70px;
                border: 1px solid rgba(255,255,255, 0.8);
                
                img{
                    width: 45px;
                    object-fit: cover;
                    height: 45px;
                    border-radius: 50%;
                    margin-right: 2rem;
                }
            }
        }


        .card-content{
            padding-top: 3rem;
            .card-title{
                font-size: 1.3rem;
                font-weight: 500;
                padding-bottom: .6rem;
            }
            .price{
                color: #6BBE92;
            }
            .l-text{
                opacity: 0.5;
            }
            .duration{
                margin-top: 1rem;
                padding-top: 1rem;
                border-top: 1px dashed rgba(255,255,255, 0.2);
                display: flex;
                justify-content: space-between;
                p{
                    display: flex;
                    align-items: center;
                    img{
                        object-fit: cover;
                        width: 18px;
                        margin-right: .6rem;
                        display: flex;
                        align-items: center;
                        justify-content: center;
                    }
                }
            }

            .cta-btn{
                width: 100%;
                a{
                    margin: .6rem 0;
                    display: inline-block;
                    width: 100%;
                    text-align: center;
                }
            }
        }
        
    }
`;
export default MainContent;
