import React from "react";
import { Slide } from 'react-slideshow-image';
import './Main.css'
import 'react-slideshow-image/dist/styles.css'

import image1 from "./img/1.jpg"
import image2 from "./image/LWL_Campus_2020.jpg"
import image3 from "./image/LWL_Campus_2020.jpg"
import {useMediaQuery} from "react-responsive";

const Desktop = ({ children }) => {
    const isDesktop = useMediaQuery({ minWidth: 992 })
    return isDesktop ? children : null
}
const Tablet = ({ children }) => {
    const isTablet = useMediaQuery({ minWidth: 768, maxWidth: 991 })
    return isTablet ? children : null
}
const Mobile = ({ children }) => {
    const isMobile = useMediaQuery({ maxWidth: 767 })
    return isMobile ? children : null
}
const Default = ({ children }) => {
    const isNotMobile = useMediaQuery({ minWidth: 768 })
    return isNotMobile ? children : null
}





const slideImages = [
    {
        url: image1,
        caption: 'Slide 1'
    },
    {
        url: image2,
        caption: 'Slide 2'
    },
    {
        url: image3,
        caption: 'Slide 3'
    },
];

const Main = () => (
    <div className="slide-container align-content-center">
        <Desktop><Tablet><Mobile><Default><Slide>
            {slideImages.map((slideImage, index)=> (
                <div className="each-slide" key={index}>
                    <div style={{'backgroundImage': `url(${slideImage.url})`}}>
                        <span>{slideImage.caption}</span>
                    </div>
                </div>
            ))}
        </Slide></Default></Mobile></Tablet></Desktop>
    </div>
);

export default Main();
